        if sec["PIE"]:
            dynamic += load_base
            if not silent:
                info("GOT[0]: {:#x} -> {:#x} ({:#x})".format(got, dynamic_org, dynamic))
        else:
            if not silent:
                info("GOT[0]: {:#x} -> {:#x}".format(got, dynamic))
