@register_command
class ByteswapCommand(GenericCommand):
    """Translate endian (little-endian <-> big-endian)."""
    _cmdline_ = "byteswap"
    _syntax_ = "{:s} [-h] VALUE".format(_cmdline_)
    _example_ = "{:s} 0xdeadbeef".format(_cmdline_)
