    _syntax_ = "{:s} [-h] [-q] [--print-each-level] [--no-merge] [--filter REGEX] [--vrange ADDR] [--prange ADDR] [--sort-by-phys] [--simple] [--trace ADDR]".format(_cmdline_)
    _example_ = "\n"
    _example_ += "{:s} --print-each-level # show all level pagetables\n".format(_cmdline_)
