class AliasesAddCommand(AliasesCommand):
    """Command to add aliases."""
    _cmdline_ = "aliases add"
    _syntax_  = "{:s} [ALIAS] [COMMAND]".format(_cmdline_)
    _example_ = "{:s} scope telescope".format(_cmdline_)
