def parse_syscall_table_defs(table_defs):
    table = []
    for line in table_defs.splitlines():
        if line == "":
            continue
        if line.startswith("#"):
            continue
        entry = line.split()
        if len(entry) == 3: # it is unimplemented
            continue
        entry[0] = int(entry[0])
        table.append(entry)
    return table


