        gef_print("| BASE2 31:24 |  |/ |L |V | LIMIT1 |P |DPL|S |  |  |  |  | BASE1 23:16 | 4byte")
        gef_print("|             |R |B |  |L | 19:16  |  |   |  |X |C |R |C |             |")
        gef_print("------------------------------------------------------------------------")
